        return new Source("{% extends 'base.html.twig' %}

{% block title %}Add New Product{% endblock %}

{% block stylesheets %}
{{ parent() }}
<style>
    body {
        font-family: Arial, sans-serif;
        line-height: 1.6;
        margin: 0;
        padding: 20px;
    }
    h1 {
        color: #333;
    }
    form {
        max-width: 400px;
        margin: 20px auto;
    }
    label {
        display: block;
        margin-bottom: 5px;
    }
    input[type=\"text\"],
    input[type=\"number\"] {
        width: 100%;
        padding: 8px;
        margin-bottom: 10px;
        border: 1px solid #ddd;
        border-radius: 4px;
    }
    button[type=\"submit\"] {
        background-color: #87CEEB;
        color: white;
        padding: 10px 15px;
        border: none;
        border-radius: 4px;
        cursor: pointer;
    }
    button[type=\"submit\"]:hover {
        background-color: #5F9EA0;
    }
    .alert-success {
        background-color: #d4edda;
        border-color: #c3e6cb;
        color: #155724;
        padding: 10px;
        margin-bottom: 15px;
        border: 1px solid transparent;
        border-radius: 4px;
    }
</style>
{% endblock %}

{% block body %}
<h1>Add New Product</h1>

{% for message in app.flashes('success') %}
    <div class=\"alert-success\">
        {{ message }}
    </div>
{% endfor %}

{{ form_start(form) }}
{{ form_row(form.designation, {'label': 'Designation:'}) }}
{{ form_row(form.univers, {'label': 'Univers:'}) }}
{{ form_row(form.price, {'label': 'Price:'}) }}
<button type=\"submit\">Add Product</button>
{{ form_end(form) }}
{% endblock %}
